import { createSlice } from "@reduxjs/toolkit"

const userSlice = createSlice({
    name: "user",
    initialState: {
        password: "",
        isAuth: false,
        user: {
            email: "",
            id: "",
            isActivated: false
        }
    },
    reducers: {
        async login(state, action) {
            state.user.email = action.payload.email
        }
    }
})

export default userSlice.reducer
