import { createSlice } from "@reduxjs/toolkit"

export const userSlice = createSlice({
    name: "user",
    initialState: {
        password: "",
        isAuth: false,
        user: {
            email: "",
            id: "",
            isActivated: false
        }
    },
    reducers: {
        login(state, action) {
            state.password = action.payload
        }
    }
})

export default userSlice.reducer
