import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useParams } from 'react-router-dom'

const SingleRoom = () => {
    const {id} = useParams()

    const user = useSelector(state => state.user)
    const {socket} = user


    useEffect(() => {
        socket.on("receive_message", (data) => {
            setMessageHistory(prev => [...prev, data])
        })
    }, [])

    const [message, setMessage] = useState("")
    const [messageHistory, setMessageHistory] = useState([])

    function sendMessage(user, message, roomId) {
        console.log("sended")
      socket.emit("send_message", {...user, message, room: `room${roomId}`})
    } 



  return (
    <div>
        <h1>Room {id}</h1>
        <input placeholder="Message..." onChange={e => setMessage(e.target.value)}></input>
        <button onClick={() => sendMessage(user, message, id)}>Send</button>
            {messageHistory.map(mess =>
            <div style={{ border: "1px solid black", margin: 30, padding: 10 }}>
                <h3>{mess.name}</h3>{mess.message}
            </div>
            )}
    </div>
  )
}

export default SingleRoom
