import { createSlice } from "@reduxjs/toolkit"

export const errorSlice = createSlice({
    name: "error",
    initialState: {
        type: "",
        text: ""
    },
    reducers: {
        setError(state, action) {
            state.type = action.payload.type
        }
    }

})

export default errorSlice.reducer
export const {setError} = errorSlice.actions
