import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import Loader from '../../components/Loader'
import useFetch from '../../hooks/useFetch'
import ChatService from '../../services/ChatService'
import styles from "./style.module.css"

const Rooms = () => {
  const [rooms, setRooms] = useState([])

  const [isLoading, fetchRooms] = useFetch(async () => {
    const res = await ChatService.getGlobalChatsInfo()
    const roomsFromServer = res.data.map(room => {
      room.id = room.id.replace("room", "")
      return room
    })
    console.log(roomsFromServer)
    setRooms(roomsFromServer)
  })

  useEffect(() => {
    fetchRooms()
  }, [])
  

  if (isLoading) return (<Loader/>)

  return (
  <div className={styles.roompage}>
    <div className={styles.rooms}>
      <Link to="1" className={styles.room}>Комната 1</Link>
      <Link to="2" className={styles.room}>Комната 2</Link>
      <Link to="3" className={styles.room}>Комната 3</Link>
    </div>
    <Link to="/home" className={styles.link}>Домой</Link>
  </div>

  )
}

export default Rooms
