import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useParams } from 'react-router-dom'
import styles from "./style.module.css"

import io from "socket.io-client"
import { SERVER_URL } from '../../http/index';
import { addRoom } from '../../store/userSlice'
const socket = io.connect(SERVER_URL)

const SingleRoom = () => {
    const {id} = useParams()

    const user = useSelector(state => state.user)
    const dispatch = useDispatch()
    
    function joinRoom(room) {
        dispatch(addRoom(room))
        socket.emit("join_room", room)
      }

      
    function sendMessage(user, message, roomId) {
        socket.emit("send_message", {...user, message, room: `room${roomId}`})
      } 

    useEffect(() => {
        socket.on("receive_message", (data) => {
            setMessageHistory(prev => [data, ...prev])
        })
    }, [])

    const [message, setMessage] = useState("")
    const [messageHistory, setMessageHistory] = useState([])




  return (
    <div className={styles.room}>
        <h1>Room {id}</h1>
        <div className={styles.history}>
            {messageHistory.map(mess =>
            <div className={styles.message}>
                <h3>{mess.name}</h3>
                {mess.message}
            </div>
            )}
        </div>
        <div className={styles.send}>
            <input placeholder="Message..." onChange={e => setMessage(e.target.value)}></input>
            <button onClick={() => sendMessage(user, message, id)}>Send</button>
        </div>
    </div>
  )
}

export default SingleRoom
