import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { Link, useParams } from 'react-router-dom'
import styles from "./style.module.css"

import io from "socket.io-client"
import api, { SERVER_URL } from '../../http/index';
import useRoom from '../../hooks/useRoom'
const socket = io.connect(SERVER_URL)

const SingleRoom = () => {
    const [message, setMessage] = useState("")
    const [messageHistory, setMessageHistory] = useState([])
    const [members, setMembers] = useState([])
    const [modalMembersVisible, setModalMembersVisible] = useState(false)

    const {id} = useParams()

    const user = useSelector(state => state.user)

    const {joinRoom, leaveRoom, sendMessage} = useRoom(setMembers, setMessage)

    function handleKeydown(e) {
        if (e.key === "Enter") {
            sendMessage(user, message, id)
        }
    }

    useEffect(() => {
        const fetchRoomInfo = async () => {
            const messagesFromServer = await api.get(`/messages/room${id}`).then(res => res.data)
            const membersFromServer = await api.get(`/messages/room${id}/members`).then(res => res.data)

            setMessageHistory(messagesFromServer.reverse())
            setMembers(membersFromServer)
        }
        fetchRoomInfo();
        socket.on("receive_message", (data) => {
            setMessageHistory(prev => [data, ...prev])
        })
    }, [])

  return (
    <div className={styles.room}>
        <Link to="/rooms" className={styles.close}>Назад</Link>
        <h1>Room {id}</h1>
        {
            members.some(member => member.id == user.id)
            ?
            <>
                <button onClick={() => setModalMembersVisible(true)}>Участники</button>
                <button onClick={() => leaveRoom(id, user.id)}>Выйти из комнаты</button>
            </>
            :
            <></>
        }
        {
            modalMembersVisible 
            ?
            <div className={styles.modal-members}>
                {members.map(member =>
                    <div key={member.id}>
                        {member.name}
                    </div>
                )}
            </div>
            :
            <></>
        }
        {   
            members.some(member => member.id == user.id)
            ?
            <>
            <div className={styles.history}>
                {messageHistory.map(mess =>
                <div className={styles.message} key={mess.id}>
                    <h3>{mess.name}</h3>
                    {mess.message}
                </div>
                )}
            </div>
            <div className={styles.send}>
                <input
                    placeholder="Введите сообщение"
                    onChange={e => setMessage(e.target.value)}
                    value={message}
                    onKeyDown={e => handleKeydown(e)}>
                </input>
                <button onClick={() => sendMessage(user, message, id)}>Отправить</button>
            </div>
            </>
            :
            <div className={styles.history}>
                <button onClick={() => joinRoom(id, user.id)}>Войти</button>
            </div>
        }
        
    </div>
  )
}

export default SingleRoom
