import { useEffect, useState } from 'react';
import './App.css';
import Registration from "./pages/Registration/index"
import Start from "./pages/Start/index"
import Home from "./pages/Home/index"
import Login from "./pages/Login/index"
import Users from "./pages/Users/index"
import SingleUser from "./pages/SingleUser/index"
import {BrowserRouter, Route, Routes, Navigate} from "react-router-dom"
import { useDispatch, useSelector } from 'react-redux';
import { checkAuth } from './store/actionCreator';
import io from "socket.io-client"

const socket = io.connect("http://localhost:5000")

function App() {


    const user = useSelector(state => state.user)
    const dispatch = useDispatch()

    useEffect(() => {
      dispatch(checkAuth())
    }, [])


    const [message, setMessage] = useState("")
    const [messageHistory, setMessageHistory] = useState([])

    function sendMessage(user, message) {
      socket.emit("send_message", {...user, message})
    } 

  return (
    <>
    <div className="App">
      <button onClick={() => sendMessage(user, message)}>Send</button>
      <div>
        {messageHistory.map(mess =>
          <div style={{ border: "1px solid black", margin: 30, padding: 10 }}>
            <h3>{mess.username}</h3>{mess.text}
          </div>
        )}
      </div>
    </div>
    </>
