    //       <Route path='users' >
    //         <Route index element={<Users/>}/>
    //         <Route path=":id" element={<SingleUser/>}/>
    //       </Route>
    //     </Route>
    //     <Route path="*" element={user.isAuth ? <Navigate to="/home"/> : <Navigate to="/start"/>} />
    //   </Routes>
    // </BrowserRouter>
  );
}

export default App;
