import { useState } from "react"

function useRoom() {
    const [members, setMembers] = useState([])
    const [message, setMessage] = useState("")


    const joinRoom = async (room) => {
        socket.emit("join_room", `room${room}`)
        const membersAfterPost = await api.post(`/messages/room${room}/members`, {user_id}).then(res => res.data)
        setMembers(membersAfterPost)
    }

    const leaveRoom = async (room) => {
        socket.emit("leave_room", `room${room}`)
        await api.delete(`/messages/room${room}/members?user=${user_id}`)
        setMembers([])
    }
      
    const sendMessage = (user, message, roomId) => {
        if (message.replace(/\s+/g, '').length === 0) return
        socket.emit("send_message", {...user, message, room: `room${roomId}`})
        setMessage("")
    } 

    return {members, setMembers, message, setMessage, joinRoom, leaveRoom, sendMessage}
}

export default useRoom
